package LAB_001;


public class Operator_increment_decreasement {
    public static void main(String[] args) {
        // TODO Auto-generated method stub
        System.out.println("Thực hiện phép tăng và giảm");
        int i = 5;int k = 5;

        i++; // Tăng giá trị của i lên 1 đơn vị. Kết quả: 6
        System.out.println("i:" + i);
        k--; // Giảm giá trị của i đi 1 đơn vị. Kết quả: 4
        System.out.println("k:" + k);

    }
}
